import React, { Component } from 'react'
import FullCalendar from "@fullcalendar/react";
import timeGridPlugin from "@fullcalendar/timegrid";
// import { CoreCalendar } from '@fullcalendar/core';
import interactionPlugin from '@fullcalendar/interaction'; // for selectable
import { createEventId } from './events'


class Calendar extends Component {
    render() {
        return (
            <div className="wrap-calendar">
                        {this.renderSidebar()}

                <div className="App" id="calendar-display">
                    <FullCalendar
                        plugins={[interactionPlugin, timeGridPlugin]}
                        initialView="timeGrid"
                        selectable="true"
                        events= {[
                            {
                              id: 'a',
                              title: 'my event',
                              start: '2020-09-17 07:20'
                            },
                            {
                                id: 'b',
                                title: 'my event',
                                start: '2020-09-17 10:20'  
                            }
                          ]}
                          select= {this.handleSelect}
                          eventContent={renderEventContent} // custom render function

                    />
                </div>
            </div>
        )
    }

    renderSidebar() {
        return (
          <div className='demo-app-sidebar'>
            <div className='demo-app-sidebar-section'>
              <h2>Instructions</h2>
              <ul>
                <li>Select dates and you will be prompted to create a new event</li>
                <li>Drag, drop, and resize events</li>
                <li>Click an event to delete it</li>
              </ul>
            </div>
            <div className='demo-app-sidebar-section'>
              <label>
                <input
                  type='checkbox'
                  checked={this.state.weekendsVisible}
                  onChange={this.handleWeekendsToggle}
                ></input>
                toggle weekends
              </label>
            </div>
            <div className='demo-app-sidebar-section'>
              <h2>All Events ({this.state.currentEvents.length})</h2>
              <ul>
                {this.state.currentEvents.map(renderSidebarEvent)}
              </ul>
            </div>
          </div>
        )
      }


    handleSelect = (selectInfo) => {
        let title = prompt('Please enter new title for the event')
        let calendarApi = selectInfo.view.calendar

        calendarApi.unselect()

        if(title){
            calendarApi.addEvent({
                id: createEventId(),
                title,
                start: selectInfo.startStr,
                end: selectInfo.endStr,
                allDay: selectInfo.allDay
            })
        }
    }
}
function renderEventContent(eventInfo) {
    return (
      <>
        <b>{eventInfo.timeText}</b>
        <i>{eventInfo.event.title}</i>
      </>
    )
  }
export default Calendar
