import { Draggable } from '@fullcalendar/interaction'
import React, { Component } from 'react'
import InputGroup from 'react-boostrap/InputGroup'
import FormControl from 'react-boostrap/FormControl'
import Button from 'react-boostrap/Button'




export class External extends Component {
    constructor(props) { 
        super(props); 

        // Setting up state 
        this.state = { 
          userInput : "", 
          list:[] 
        } 
    }

    // Set a user input value
    updateInput(value){
        this.setState({
            userInput: value,
        });
    }

    // Add item if user input is not empty
    addItem(){
        if(this.state.userInput!==''){
            const userInput = {
                id: Math.random(),
                value: this.state.userInput
            };

            // Update list
            const list = [...this.state.list];
            list.push(userInput);

            // reset
            this.setState({
                list,
                userInput: ""
            });
        }
    }

    render() {
        return (
            <div id="external-events">
                <h4>Draggable events</h4>
                <div class='fc-event fc-h-event fc-daygrid-event fc-daygrid-block-event'>
                    <div class='fc-event-main'>My Event 1</div>
                </div>
                <div class='fc-event fc-h-event fc-daygrid-event fc-daygrid-block-event'>
                    <div class='fc-event-main'>My Event 2</div>
                </div>
                <div class='fc-event fc-h-event fc-daygrid-event fc-daygrid-block-event'>
                    <div class='fc-event-main'>My Event 3</div>
                </div>
                <div class='fc-event fc-h-event fc-daygrid-event fc-daygrid-block-event'>
                    <div class='fc-event-main'>My Event 4</div>
                </div>
                <div class='fc-event fc-h-event fc-daygrid-event fc-daygrid-block-event'>
                    <div class='fc-event-main'>My Event 5</div>
                </div>

                <InputGroup className="mb-3"> 
          <FormControl 
            placeholder="add item . . . "
            size="lg"
            value = {this.state.userInput} 
            onChange = {item => this.updateInput(item.target.value)} 
            aria-label="add something"
            aria-describedby="basic-addon2"
          /> 
          <InputGroup.Append> 
            <Button 
              variant="dark"
              size="lg"
              onClick = {()=>this.addItem()} 
              > 
              ADD 
            </Button> 
          </InputGroup.Append> 
        </InputGroup> 
            </div>
        )
    }
   componentDidMount(){
        let draggableEl = document.getElementById('external-events');
        new Draggable(draggableEl, {
            itemSelector: '.fc-event',
            eventData: function(eventEl) {
              return {
                title: eventEl.innerText
              };
            }
          });
   }
}

export default External
